mapper.select:
select e.c_id as e_id, e.c_date as e_date, e.c_start as e_startTime, e.c_end as e_endTime, e.c_type as e_type, e.c_len as e_length, e.c_comment as e_comment, e.c_user_id as e_user_id, e.c_status as e_status, e.c_meal_lunch as e_meal_lunch, e.c_meal_supper as e_meal_supper, e.c_approval_user_id as e_approval_user_id, e.c_approval_time as e_approval_time, e.c_expense_item_id as e_expense_item_id, u.c_id as u_id, u.c_no as u_no, u.c_name as u_name, a.c_id as au_id, a.c_no as au_no, a.c_name as au_name from extra_time_tbl as e left join user_tbl as u on e.c_user_id=u.c_id left join user_tbl as a on e.c_approval_user_id=a.c_id left join user_group_tbl as ug on ug.c_user_id=e.c_user_id
and e.c_id = #{id}
and e.c_status = #{status}
and u.c_no like '%${user.no}%'
and u.c_no=#{user.no}
and e.c_date >= #{cond.startDate}
and e.c_date <= #{cond.endDate}
and ug.c_group_id = #{cond.groupId}
order by
u.c_no ${orderCond.flag}
e.c_date ${orderCond.flag}

    	select
    		ifnull(sum(c_len), 0.0)
    	from extra_time_tbl as e

    		e.c_date >= #{cond.startDate}
    		and e.c_date <= #{cond.endDate}
			and c_user_id=#{cond.userId}
