mapper.select:
select r.c_id as r_id, r.c_date_start as r_start, r.c_date_end as r_end, r.c_len as r_len, r.c_type as r_type, r.c_summary as r_summary, r.c_status as r_status, r.c_approval_time as r_approvalTime, appu.c_id as appu_id, appu.c_name as appu_name, appu.c_no as appu_no, u.c_id as u_id, u.c_name as u_name, u.c_no as u_no from rest_tbl as r left join user_tbl as u on u.c_id=r.c_user_id left join user_tbl as appu on appu.c_id=r.c_approval_user_id left join user_group_tbl as ug on ug.c_user_id=r.c_user_id
and u.c_no like '%${user.no}%'
and u.c_no=#{user.no}
and r.c_user_id = #{cond.userId}
and r.c_date_start <= #{cond.date} and r.c_date_end >= #{cond.date}
and r.c_date_start >= #{dateStart}
and r.c_date_start <= #{dateEnd}
and r.c_id = #{id}
and r.c_status in
#{status}
and ug.c_group_id = #{cond.groupId}
order by r.c_date_start

    	select
    		ifnull(sum(c_len), 0.0)
    	from rest_tbl as r

			r.c_date_start >= #{cond.dateStart}
    		and r.c_date_end <= #{cond.dateEnd}
			and c_user_id=#{cond.userId}
